import { ERROR, SUCCES } from "../../components/utils/tools";

export const notifications = (state = {}, action: any) => {
  switch (action.type) {
    case ERROR:
      return {
        ...state,
        error: true,
        message: action.payload,
			};
			case SUCCES:
				return {
					...state,
					error: true,
					message: action.payload,
				};
    default:
      return state;
  }
};
