import { ERROR, SUCCES } from "../../components/utils/tools";

export const errorNotification = (message: string) => {
  return {
    type: ERROR,
    payload: message,
  };
};

export const succesNotification = (message: string) => {
  return {
    type: SUCCES,
    payload: message,
  };
};
